package base;

/**
 * 各数据类型的默认值
 */
public class DefaultValue {
    boolean b;
    char c;
    byte by;
    short s;
    int str;
    long l;
    short sh;
    double d;

    public static void main(String[] args) {
        // TODO Auto-generated method stub

        DefaultValue defaultValue = new DefaultValue();

        System.out.println("boolean=" + defaultValue.b);
        System.out.println("char=" + defaultValue.c);
        System.out.println("byte=" + defaultValue.by);
        System.out.println("short=" + defaultValue.s);
        System.out.println("int=" + defaultValue.str);
        System.out.println("long=" + defaultValue.l);
        System.out.println("short=" + defaultValue.sh);
        System.out.println("double=" + defaultValue.d);

    }

}
